void help(const char *args)
{
	(void)(args);

	print("Available commands: ");
	
	for(uint16_t i = 0; i < N_CMDS; i++) {
		print(commands[i].cmd);
		print(", ");
	}

	println("");
}

void clear(const char *args)
{
	(void)(args);	
	clear_screen();
}

void time(const char *args)
{
	(void)(args);

	struct DateTime t = get_rtc();

	char buf[20];

	print(pad(2, '0', itoa(t.hour, buf)));
	print(":");
	print(pad(2, '0', itoa(t.minute, buf)));

	print(" ");

	print(pad(2, '0', itoa(t.day, buf)));
	print(".");
	print(pad(2, '0', itoa(t.month, buf)));
	print(".");
	print(itoa(t.year, buf));
		
	println("");
}

void sleep(const char *args)
{
	(void)(args);
	wait(100);
}


bool is_q(const char c);
void exit_draw(const char c);

struct KeyboardCallback kc = { is_q, exit_draw };

bool is_q(const char c)
{
	return c == 'q';
}

void exit_draw(const char c)
{
	(void)(c);
	switch_tm();
	drop_callback(kc);
}

void draw(const char *args)
{
	(void)(args);

	switch_gm();

	uint16_t offx = VGA_WIDTH / 2 - 50;
	uint16_t offy = VGA_HEIGHT / 2 - 50;;

	for(uint16_t x = 0; x < 100; x++) {
		for(uint16_t y = 0; y < 100; y++) {
			putpixel(x + offx, y + offy, lenna[(y * 100) + x]);
		}
	}

	register_callback(kc);
}
